concept.theorem:
\startformula
\SystemR{
\NC f→\L_aℓ\NR
\NC g→\L_aℓ'}⟹\System{
\NC f+g→\L_a ℓ+ℓ'\NR
\NC f×g→\L_a ℓ×ℓ'
}
\stopformula

concept.definition:

\startformula
f \text{ est }\Choices{
	\NC\text{croissante }\NR
	\NC\text{strictement croissante }\NR
	\NC\text{décroissante }\NR
	\NC\text{strictement décroissante }
} \text{ sur }I \ ⟺\ \Choices{
	\NC f(y) ⩾ f(x)\NR
	\NC f(y) > f(x)\NR
	\NC f(y) ⩽ f(x)\NR
	\NC f(y) \Le f(x)
}\qquad(x\Le y\text{ dans }I)
\stopformula

combination.theorem:
\startformula
\Align{[align={right,middle,left}]
	\NC f\text{ converge en } a^-\NC \ ⟺\ \NC  ∃r>0: f\text{ est majorée sur } ]a-r,a]\NR
	\NC \lim_{a^-} f \NC = \NC \sup\{f(x):x\Le a\}
}
\stopformula

\startformula
\Align{[align={right,middle,left}]
	\NC f\text{ converge en } a^-\NC \ ⟺\ \NC ∃r>0:: f\text{ est minorée sur }[a,a+r[\NR
	\NC \lim_{a^-} f \NC = \NC \inf\{f(x):x⩽a\}
}
\stopformula

combination.property:
\startformula
\SystemR{
	\NC f ≪\L_D h\NR
	\NC g ≪\L_D h 
} \ ⟹ \  f + g ≪\L_D h
\stopformula

\startformula	
\SystemR{
	\NC f ≪\L_D G\NR
	\NC g ≪\L_D G
} \ ⟹ \  fg ≪\L_D FG
\stopformula
