concept.property:
\startformula
u \text{ est bornée} \ ⟺\  \mc T_d(u) \text{ est bornée}
\stopformula

\startformula
x+iy \text{ est bornée } \ ⟺\  x \Et y \text{ sont bornées }
\stopformula

concept.definition:
\startformula
u→ℓ ⟺ \Choices{
	\NC u \text{ tends vers } ℓ\NR
	\NC \lim(u)=ℓ
}\stopformula

\startformula
\startAlign
\NC S=\sup A \NC \ ⟺\ \NC \System{
  \NC A ⩽ S\NR
  \NC ∃a∈A^ℕ, \ \lim a=S}\NR
\NC s= \inf A \NC \ ⟺\  \NC \System{
  \NC s ⩽ A \NR
  \NC ∃a∈A^ℕ, \ \lim a=S}
\stopAlign
\stopformula

concept.property:
\startformula
u \text{ est bornée} \ ⟺\  u \text{ est minorée et majorée}
\stopformula

concept.definition:
\startformula
\Align{
\NC u→ℓ^- \ ⟺\  (u_n⩽ℓ\Quand n→∞)\NR
\NC u→ℓ^+ \ ⟺\  (u_n⩾ℓ\Quand n→∞)
}
\stopformula

\startformula 
\Align{
\NC u → +∞  \NC ⟺ \NC(∀ M ∈ ℝ, ∃ N ∈ ℕ:∀n⩾N,u_n ⩾ M)\NR
\NC u → -∞  \NC ⟺ \NC(∀ m ∈ ℝ, ∃ N ∈ ℕ:∀n⩾N,u_n ⩽ m)
}
\stopformula


\startformula
u \text{ est }\Choices{
	\NC\text{croissante }\NR
	\NC\text{strictement croissante }\NR
	\NC\text{décroissante }\NR
	\NC\text{strictement décroissante }
}\ ⟺\ \Choices{
	\NC u_{n+1} ⩾ u_n\NR
	\NC u_{n+1} > u_n\NR
	\NC u_{n+1} ⩽ u_n\NR
	\NC u_{n+1} \Le u_n
}\qquad(n⩾0)
\stopformula

\startformula
u\Et v \text{ sont adjacentes}\ ⟺\ \System{
	\NC u \text{ croissante}\NR
\NC v \text{ décroissante}\NR
\NC v-u→0}
\stopformula

combination.property:
\startformula
\SystemR{
	\NC u ≪ w\NR
	\NC v ≪ w
} \ ⟹ \  u+v ≪ w
\stopformula

\startformula	
\SystemR{
	\NC u ≪ U\NR
	\NC v ≪ V
} \ ⟹ \  uv ≪ UV
\stopformula

\startformula
\SystemR{
	\NC u ≺ w\NR
	\NC v ≺ w
} \ ⟹ \  u+v ≺ w
\stopformula

\startformula	
\SystemR{
	\NC u ≺ U\NR
	\NC v ≺ V
} \ ⟹ \  uv ≺ UV
\stopformula

concept.remark:
Cette relation est reflexive et symétrique : pour $u,v∈𝕂^N$, on a 
\startformula
\Align{
	\NC u∼v \ ⟺\ v∼u\qquad\NC\text{(symétrie)}\NR
	\NC u∼u\NC\text{(reflexivité)}
}
\stopformula

combination.property:
\startformula	
\SystemR{
	\NC u ∼ U\NR
	\NC v ∼ V
} \ ⟹ \  uv ∼ UV
\stopformula
